mapper.select:

select *
from (
    select recNum, board_id, title, content, writedate, m_id, m_nickname, thumbnail
    from (
        select rownum as recNum, board_id, title, content, writedate, m_id, m_nickname, thumbnail
        from (
            select b.board_id, b.title, b.content, b.writedate, m.m_id, m.m_nickname, b.thumbnail
            from board b, member m
            where b.m_id = m.m_id
            order by board_id desc)))
WHERE recNum BETWEEN (#{page} - 1) * #{perPageNum} + 1 AND #{page} * #{perPageNum}


select *
from (
    select  b.board_id, b.title, b.content, b.writedate, b.image, m.m_id, m.m_nickname
    from board b, member m
    where b.m_id = m.m_id)
where board_id = #{board_id}
