mapper.select:
SELECT	*
FROM	member
WHERE	m_id	= #{m_id}
AND		m_pw	= #{m_pw}

SELECT COUNT(*)
FROM member
WHERE m_name = #{m_name} AND m_tel = #{m_tel}

SELECT COUNT(*)
FROM member
WHERE m_name = #{m_name} AND m_nickname = #{m_nickname}

SELECT m_id
FROM member
WHERE m_tel = #{m_tel}

SELECT m_id
FROM member
WHERE m_nickname = #{m_nickname}

SELECT COUNT(*)
FROM member
WHERE m_id = #{m_id} AND m_tel = #{m_tel}

SELECT m_pw
FROM member
WHERE m_id = #{m_id}

SELECT	COUNT(*)
FROM	member
WHERE	m_id = #{m_id}

SELECT	COUNT(*)
FROM	member
WHERE	m_nickname = #{m_nickname}
