mapper.select:
select reply_num, b_id, r_writer, content, writeDate
from reply
where b_id = #{board_id}

mapper.update:
update reply set
	r_writer = #{r_writer},
	content = #{content}
where replyNum = #{replyNum}
	and b_id = ${board_id}

mapper.delete:
delete from tbl_reply
where reply_num = #{replyNum}
	and b_id = ${board_id}
