mapper.update:

UPDATE cart
SET count = #{count}
WHERE cart_id = #{cart_id}

mapper.select:

SELECT product_id
FROM product
WHERE product_name = #{product_name}


SELECT order_number
FROM product_order
WHERE m_id = #{m_id}

mapper.update:

UPDATE product_order
SET order_complaint = #{reason}, order_status = #{status}
WHERE order_id = #{id}

mapper.select:

SELECT m_id, product_id, receiver_name, receiver_phonenum, zipcode, address, address_detail, count, final_price
FROM product_order
WHERE order_id = #{order_id}

SELECT order_id, m_id, product_id, order_number, receiver_name, receiver_phonenum, zipcode, address, address_detail, order_date, order_status, order_complaint, count, final_price FROM (
SELECT ROWNUM as rnum, order_id, m_id, product_id, order_number, receiver_name, receiver_phonenum, zipcode, address, address_detail, order_date, order_status, order_complaint, count, final_price FROM product_order WHERE m_id = #{member} AND order_complaint IS NULL
)
WHERE rnum BETWEEN (#{page} - 1) * #{perPageNum} + 1 AND #{page} * #{perPageNum}


SELECT * 
FROM product_order
WHERE order_number = #{order_num , jdbcType=VARCHAR}
